import React from "react"
import PropTypes from "prop-types"
import axios from "axios"

export default class FormComponent extends React.Component {

  constructor(props){
    super(props)
    this.state = {
      numberField: ""
    }
  }

  handleChange(e){
    this.setState({numberField: e.target.value})
  }

  postContent(){

    var body = {
      number: 2,
    }

    axios({
      method: "post",
      url: "http://localhost:8080/checker",
      data: body,
    })
      .then(function (response) {
        //handle success
        alert(response.data["isPrime"])
        console.log(response);
      })
      .catch(function (response) {
        //handle error
        alert("An error occurred")
        console.log(response);
      });
  }

  deleteNumber(){
    this.setState({numberField: ""})
  }

  render() {
    return(
      <div>
        <input type="number" value={this.state.numberField} onChange={this.handleChange.bind(this)}></input>
        <button onClick={this.postContent.bind(this)}>post</button>
      </div>
    )
  }
}
